import React, { useEffect, useState } from "react";
import { CarTypeInterface } from "./Components/CarTypeInterface";
import CarTypeContext from "./Components/CarTypeContext";
import CarTypeComp from "./Components/CarTypeComp";
import { ProductInterface } from "./Components/Interfaces/ProductInterface";
import { ManufacturerInterface } from "./Components/Interfaces/ManufacturerInterface";
import { modelInterface } from "./Components/Interfaces/ModelInterface";
import ModelInp from "./Components/ModelInp";
import Manufacturer from "./Components/Manufacturer";
import ProductComponent from "./Components/Product";
import "bootstrap/dist/css/bootstrap.css";
import "./Data/Pages.css";
import Pagination from "./Components/Paginations";
import "C:/Users/Zura/OneDrive/Desktop/myauto/src/Components/SearchResults.css";
import "C:/Users/Zura/OneDrive/Desktop/myauto/src/Components/Header.css";
import Header from "./Components/Header";
import SortDropdown from "./DropDows";

const App: React.FC = () => {
  const [categories, setCategories] = useState<CarTypeInterface[]>([]);
  const [cartegory, setCartegory] = useState<number>(0);

  const [manArr, setManArr] = useState<ManufacturerInterface[]>([]);
  const [currentMan, setCurrentMan] = useState<string[]>([]);

  //should get man id and then fetch from
  const [modelArr, setModelArr] = useState<modelInterface[]>([]);
  const [chosenModel, setChosen] = useState<number>(0);
  const [products, setProducts] = useState<ProductInterface[]>([]);

  //-------------------------------------------------------//
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  const totalPage = Math.ceil(products.length / itemsPerPage);

  const fetchCategories = async (): Promise<void> => {
    try {
      const response: Response = await fetch(
        "https://api2.myauto.ge/ka/cats/get"
      );
      const data = await response.json();
      setCategories(data.data);
    } catch (error) {
      console.error("Error while fetching categories", error);
    }
  };

  const fetchManufacturers = async (): Promise<void> => {
    try {
      const response: Response = await fetch(
        "https://static.my.ge/myauto/js/mans.json"
      );
      const data = await response.json();
      setManArr(data);
    } catch (error) {
      console.error("Error while fetching categories", error);
    }
  };
  const fetchProducts = async (): Promise<void> => {
    try {
      let page = 1;
      let hasMorePages = true;

      while (hasMorePages && page <= 50) {
        const response: Response = await fetch(
          `https://api2.myauto.ge/ka/products/?Page=${page}`
        );
        const data = await response.json();

        if (data.data.items.length > 0) {
          setProducts((prevProducts) => [...prevProducts, ...data.data.items]);
          page++;
        } else {
          hasMorePages = false;
        }
      }
    } catch (error) {
      console.error("Error while fetching products", error);
    }
  };

  useEffect(() => {
    fetchProducts();
    fetchCategories();
    fetchManufacturers();
  }, []);

  const productsToDisplay = products.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const handlePageClick = (pageNum: number) => setCurrentPage(pageNum);

  // console.log(categories.length)
  if (categories.length === 0) {
    return <div>LOADING</div>;
  } else {
    return (
      <div className="searchResult">
        <CarTypeContext.Provider
          value={{
            categories,
            setCategories,
            cartegory,
            setCartegory,
            manArr,
            setManArr,
            currentMan,
            setCurrentMan,
            modelArr,
            setModelArr,
            chosenModel,
            setChosen,
            products,
            setProducts,
          }}
        >
          <Header />
          <div className="Group33738">
            <div>
              <SortDropdown />
            </div>
          </div>

          {productsToDisplay.map((product) => {
            return (
              <>
                <div className="row">
                  <ProductComponent product={product} />
                </div>
              </>
            );
          })}
          <Pagination
            currentPage={currentPage}
            totalPage={totalPage}
            handlePageClick={handlePageClick}
          />
        </CarTypeContext.Provider>
      </div>
    );
  }
};

export default App;
