import React, { FC, useContext, useEffect,useState } from 'react'
import CarTypeContext from './CarTypeContext'
import { CarTypeInterface } from './CarTypeInterface';
import { ManufacturerInterface } from './Interfaces/ManufacturerInterface';


const CarTypeComp:FC = () => {
  const {categories,setCategories} = useContext(CarTypeContext);
  const {cartegory,setCartegory} = useContext(CarTypeContext)
  
  const [wholeCategories,setWholeCategories] = useState<CarTypeInterface[]>([]);
  


  useEffect(()=>{
    setWholeCategories(categories)
  },[])
  
  
  const setCat = (id:number):void =>{
    setCartegory(id);
  }
  
  
  useEffect(()=>{
    let newCategories:CarTypeInterface[] = wholeCategories.filter((e)=>e.category_type ==cartegory)
    if(wholeCategories.length >0){
      setCategories(newCategories)
    }
  },[cartegory])
  

  return (
    <div className='cartegory'>
      <button onClick={()=>setCat(0)}>Car</button>
      <button onClick={()=>setCat(1)}>Goroza</button>
      <button onClick={()=>setCat(2)}>Moto</button>
          
      <div>{categories[0].category_id}</div>
      <div>{categories[0].category_type}</div>
      <div>{categories[0].has_icon}</div>
      <div>{categories[0].seo_title}</div>
      <div>{categories[0].title}</div>

    </div>
  )
}

export default CarTypeComp
