import React, { useContext, useState, useEffect } from "react";
import CarTypeContext from "../CarTypes/CarTypeContext";
import "./Manufacturer.css";

const PriceFilter: React.FC = () => {
  const [minPrice, setMinPrice] = useState<string>("");
  const [maxPrice, setMaxPrice] = useState<string>("");

  const { PriceRange, setPriceRange } = useContext(CarTypeContext);

  useEffect(() => {
    let min = parseInt(minPrice);
    let max = parseInt(maxPrice);
    setPriceRange([min, max]);
  }, [minPrice, maxPrice]);

  const handleMinPriceChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setMinPrice(event.target.value);
  };

  const handleMaxPriceChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setMaxPrice(event.target.value);
  };

  return (
    <div className="">
      <div className="price-cont d-flex justify-content-between">
        <input
          className="min"
          type="text"
          value={minPrice}
          onChange={handleMinPriceChange}
          placeholder="დან"
        />
        <input
          className="max "
          type="text"
          value={maxPrice}
          onChange={handleMaxPriceChange}
          placeholder="მდე"
        />
      </div>
    </div>
  );
};

export default PriceFilter;
