import React from "react";

interface PaginationProps {
  currentPage: number;
  totalPage: number;
  handlePageClick: (pageNum: number) => void;
}

const Pagination: React.FC<PaginationProps> = ({
  currentPage,
  totalPage,
  handlePageClick,
}) => {
  return (
    <div className="pages">
      {currentPage > 1 && (
        <>
          <span
            className="d-flex p-12px cursor-pointer"
            onClick={() => handlePageClick(1)}
          >
            {/* SVG code for first page */}
          </span>
          <span
            className="d-flex p-12px cursor-pointer"
            onClick={() => handlePageClick(currentPage - 1)}
          >
            {/* SVG code for previous page */}
          </span>
        </>
      )}
      {[...Array(totalPage)].map((_, idx) => {
        const pageNumber = idx + 1;
        let startPage = 1;
        if (currentPage > 4) {
          startPage = currentPage - 3;
        }
        if (currentPage < totalPage - 3 && startPage + 6 > totalPage) {
          startPage = totalPage - 6;
        }
        if (pageNumber >= startPage && pageNumber < startPage + 7) {
          return (
            <button
              className={`buttons${
                pageNumber === currentPage ? " active" : ""
              }`}
              key={pageNumber}
              onClick={() => handlePageClick(pageNumber)}
            >
              {pageNumber}
            </button>
          );
        }
        return null;
      })}
      {currentPage < totalPage && (
        <>
          <span
            className="d-flex p-12px cursor-pointer"
            onClick={() => handlePageClick(currentPage + 1)}
          >
            {/* SVG code for next page */}
          </span>
          <span
            className="d-flex p-12px cursor-pointer"
            onClick={() => handlePageClick(totalPage)}
          >
            {/* SVG code for last page */}
          </span>
        </>
      )}
    </div>
  );
};

export default Pagination;
