import React, { ChangeEvent, useState } from "react";
import { useContext } from "react";
import CarTypeContext from "../CarTypes/CarTypeContext";
import "./Manufacturer.css";

const Manufacturer: React.FC = () => {
  const { manArr, setManArr } = useContext(CarTypeContext);
  const { currentMan, setCurrentMan } = useContext(CarTypeContext);
  const [dropped, setDropped] = useState<boolean>(false);
  const [selectedValues, setSelectedValues] = useState<string[]>([]);
  const [inputValue, setInputValue] = useState<string>("");

  const handleChange = (event: ChangeEvent<HTMLInputElement>): void => {
    const checkboxValue = event.target.value;
    if (event.target.checked) {
      setCurrentMan([...currentMan, checkboxValue]);
    } else {
      const updatedChecks = currentMan.filter(
        (value) => value !== checkboxValue
      );
      setCurrentMan(updatedChecks);
    }
  };

  const handleLabelMouseDown = (
    event: React.MouseEvent<HTMLLabelElement>
  ): void => {
    event.preventDefault();
  };

  const handleInputChange = (event: ChangeEvent<HTMLInputElement>): void => {
    setInputValue(event.target.value);
  };

  const showDropdown = (): void => {
    if (!dropped) {
      setDropped(true);
    }
  };

  const handleBlur = (): void => {
    setDropped(false);
  };

  const handleCheckboxMouseDown = (
    event: React.MouseEvent<HTMLInputElement>
  ): void => {
    event.preventDefault();
  };

  const resetVal = (): void => {
    setCurrentMan([]);
    setSelectedValues([]);
    setInputValue("");
    setDropped(false);
  };

  const updateSelectedValues = (): void => {
    const updatedValues = manArr
      .filter((man) => currentMan.includes(man.man_name))
      .map((man) => man.man_name);
    setSelectedValues(updatedValues);
    setInputValue(updatedValues.join(", "));
  };

  // Update selected values whenever currentMan changes
  useEffect(() => {
    updateSelectedValues();
  }, [currentMan]);

  return (
    <div>
      <div className="form-group ">
        <div tabIndex={0} onFocus={showDropdown} onBlur={handleBlur}>
          <div className="input-group">
            <input
              className="form-control border-1"
              type="text"
              placeholder="მწარმოებელი"
              value={inputValue}
              onChange={handleInputChange}
            />
            <button className="btn  input-group-append" onClick={resetVal}>
              X
            </button>
          </div>

          <div
            className={
              dropped ? "checkbox-container " : "checkbox-container d-none "
            }
          >
            <div className={"scrollable-checkbox border-0 "}>
              {manArr.map((man) => (
                <div className="form-check" key={man.man_name}>
                  <input
                    className="form-check-input"
                    type="checkbox"
                    name={man.man_name}
                    id={man.man_name}
                    onMouseDown={handleCheckboxMouseDown}
                    value={man.man_name}
                    onChange={handleChange}
                    checked={currentMan.includes(man.man_name)}
                  />
                  <label
                    onClick={handleLabelMouseDown}
                    className="form-check-label w-100"
                    htmlFor={man.man_name}
                  >
                    {man.man_name}
                  </label>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Manufacturer;
