import React, { useContext, useState } from "react";
import CarTypeContext from "../CarTypes/CarTypeContext";
import CombinedFilter from "./CombinedFilter";
import { ProductInterface } from "../Interfaces/ProductInterface";
import { ManufacturerInterface } from "../Interfaces/ManufacturerInterface";
import { modelInterface } from "../Interfaces/ModelInterface";
import { CarTypeInterface } from "../Interfaces/CarTypeInterface";

const FilterButton: React.FC = () => {
  const [filteredProd, setFilteredprod] = useState<ProductInterface[]>([]);
  const {
    products,
    manArr,
    modelArr,
    currentMan,
    chosenModel,
    chosenCategories,
    PriceRange,
    categories, // Add categories from context
  } = useContext(CarTypeContext);

  const handleClick = (): void => {
    const filteredProducts = CombinedFilter(
      products,
      manArr,
      modelArr,
      currentMan,
      chosenModel,
      chosenCategories,
      PriceRange,
      categories // Pass categories to CombinedFilter
    );
    setFilteredprod(filteredProducts);
  };

  return (
    <div>
      <button onClick={handleClick}>FILTER</button>
    </div>
  );
};

export default FilterButton;
